generated.css:
*{
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}
*::before,*::after{box-sizing: border-box;}
ul,ol{list-style: none;}
a{color: inherit;text-decoration: none;}
img{
    max-width: 100%;
    max-height: 100%;
}

/* style */

body{
    background-color: #eee;

}

.globalHeader{
    margin: 34px;
}
.searchForm{
    display: flex;
    justify-content: space-between;
}
.searchForm >input{
    width: 100%;
    padding: 0 10px;
    border: 1px solid #ddd;
    border-radius: 15px;
}
.searchForm >button{
    white-space: nowrap;
    margin-left: 10px;
    height: 40px;
    padding: 0 20px;
    border: none;
    background: #2281AA;
    border-radius: 15px;
    color: white;
    font-size: 16px;
    cursor: pointer;

}

.siteList{
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    align-items:center;
    user-select: none


}
.siteList >li{
    display: flex;
    justify-content: flex-start;
    margin: 10px;

}
.siteList .site{
    position: relative;
    width: 150px;
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items:center;
    background: white;
    border-radius: 15px;
    cursor: pointer;
    user-select: none


}

.siteList .site >.logo{
    width: 64px;
    height: 64px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 64px;


}
.siteList .site >.link{
    font-size: 16px;

}

.siteList .site >.close{
    position: absolute;
    top: 0;
    left:0;
    font-size: 16px;
    display: none;

}

.siteList .site:hover >.close{
    position: absolute;
    top: 0;
    left:0;
    font-size: 16px;
    display: block;

}
.siteList .addButton .icon{
    width: 50px;
    height: 64px;
}
.siteList .addButton{
    width: 150px;
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items:center;
    background: white;
    border-radius: 15px;
    cursor: pointer;
}
.siteList .text{
    font-size: 16px;
    }
